// Instantiate
var datatable = new DataTable("table", {
	plugins: {
		editable: {
			enabled: true,
			contextMenu: true,
			hiddenColumns: true,
			menuItems: [
				{
					text: "<span class='mdi mdi-lead-pencil'></span> Edit Cell",
					action: function() {
						this.editCell();
					}
				},
				{
					text: "<span class='mdi mdi-lead-pencil'></span> Edit Row",
					action: function() {
						this.editRow();
					}
				},
				{
					separator: true
				},
				{
					text: "<span class='mdi mdi-delete'></span> Remove",
					action: function() {
						if ( confirm("Are you sure?") ) {
							this.removeRow();
						}
					}
				}
			]
		}
	}
});